INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (13,'淞沪会战','Battle of Shanghai','上海',31.2304160,121.4737010,'1937-08-13','1937-11-26',106,'张治中、冯玉祥','松井石根',700000,300000,300000,40000,'defeat','淞沪会战是中国抗日战争中第一场大型会战，也是整个中日战争中进行的规模最大、战斗最惨烈的一场战役。此战粉碎了日本\"三个月灭亡中国\"的计划，为中国争取了宝贵的战略准备时间。','淞沪会战是抗日战争全面爆发后的第一场大型会战。1937年8月13日，日军进攻上海，中国军队奋起抵抗。战役历时三个月，中国军队浴血奋战，给日军以沉重打击。虽然最终上海失守，但这场战役打破了日本\"三个月灭亡中国\"的狂妄计划，极大地鼓舞了全国人民的抗战信心。',5,1580,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (14,'平型关大捷','Battle of Pingxingguan','山西灵丘县',39.4422000,114.2340000,'1937-09-25','1937-09-25',1,'林彪、聂荣臻','板垣征四郎',15000,4000,600,1000,'victory','平型关大捷是全面抗战爆发后中国军队主动寻歼敌人的第一个大胜仗，打破了\"日军不可战胜\"的神话，极大地振奋了全国军民的抗战信心。','1937年9月25日，八路军第115师在平型关伏击日军第5师团第21旅团，歼敌1000余人，击毁汽车100余辆，缴获大量军用物资。这是全面抗战以来中国军队取得的第一个大胜利，打破了日军不可战胜的神话。',5,2340,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (15,'台儿庄战役','Battle of Taierzhuang','山东枣庄台儿庄',34.5628000,117.7342000,'1938-03-16','1938-04-07',23,'李宗仁、孙连仲','矶谷廉介',400000,60000,30000,20000,'victory','台儿庄大捷是抗战以来正面战场取得的最大胜利，极大地鼓舞了全国军民的抗战士气，坚定了抗战必胜的信念。','台儿庄战役是抗日战争初期中国军队在徐州会战中取得的一次重大胜利。1938年3月，日军矶谷师团进攻台儿庄，中国军队在李宗仁指挥下顽强抵抗，最终歼敌1万余人。这次胜利极大地鼓舞了全国人民的抗战信心。',5,1890,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (16,'武汉会战','Battle of Wuhan','湖北武汉',30.5930990,114.3053930,'1938-06-11','1938-10-27',139,'陈诚、薛岳','畑俊六',1100000,350000,254000,40000,'stalemate','武汉会战是抗战初期规模最大、时间最长的战役，大量消耗了日军有生力量，为持久抗战奠定了基础。','武汉会战历时4个半月，是抗日战争战略防御阶段规模最大、时间最长、歼敌最多的一次战役。中国军队虽然最终撤出武汉，但大量消耗了日军有生力量，为战略相持阶段的到来创造了条件。',5,1670,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (17,'长沙会战','Battle of Changsha','湖南长沙',28.2282090,112.9388140,'1939-09-14','1944-08-08',1795,'薛岳、罗卓英','冈村宁次',300000,120000,70000,60000,'victory','长沙会战前三次均以中国军队胜利告终，是抗战中期正面战场的重大胜利，沉重打击了日军的嚣张气焰。','长沙会战共进行了四次，前三次会战中国军队均取得胜利。特别是第三次长沙会战，中国军队歼敌5万余人，击毙日军中将指挥官，是抗战以来正面战场的重大胜利。',5,1450,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (18,'百团大战','Hundred Regiments Offensive','华北地区',37.8735320,112.5623980,'1940-08-20','1940-12-05',108,'彭德怀、左权','多田骏',400000,150000,17000,20000,'victory','百团大战是抗战期间八路军在华北地区发动的规模最大、持续时间最长的战役，沉重打击了日军的\"囚笼政策\"。','百团大战是八路军在华北敌后战场发动的一次大规模进攻战役。参战部队达105个团约40万人，攻击目标主要是破坏日军的交通线，摧毁日伪军据点。此战沉重打击了日军的\"囚笼政策\"，振奋了全国军民的抗战信心。',5,2100,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (19,'枣宜会战','Battle of Zaoyang-Yichang','湖北枣阳、宜昌',31.8917000,112.7688000,'1940-05-01','1940-06-18',49,'张自忠、李宗仁','园部和一郎',400000,150000,50000,7000,'defeat','枣宜会战中，上将张自忠壮烈殉国，成为抗战中牺牲的最高将领，其精神永垂不朽。','枣宜会战是1940年日军为控制长江交通、切断通往重庆的运输线而发动的战役。中国军队奋勇抵抗，第33集团军总司令张自忠将军在战斗中壮烈殉国，成为抗战期间牺牲的最高将领。',5,1780,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (20,'中国远征军入缅作战','Chinese Expeditionary Force in Burma','缅甸',21.9162210,95.9559740,'1942-03-01','1942-08-01',154,'杜聿明、戴安澜','饭田祥二郎',100000,60000,50000,4500,'defeat','远征军入缅作战是中国军队首次出国作战，虽然失利但展现了中国军人的英勇气概，戴安澜将军殉国缅甸。','1942年，应英国请求，中国组建远征军入缅作战。远征军在极其艰苦的条件下与日军作战，第200师师长戴安澜将军在战斗中壮烈牺牲。虽然此次作战未能达到预期目标，但展现了中国军人的英勇气概。',4,1560,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (21,'常德会战','Battle of Changde','湖南常德',29.0316730,111.6984970,'1943-11-02','1943-12-20',49,'孙连仲、余程万','横山勇',100000,70000,50000,20000,'victory','常德会战中，第74军57师8000余人坚守常德16天，展现了中国军人宁死不屈的精神。','1943年11月，日军进攻常德。第74军57师师长余程万率8000余人坚守常德城16天，与数倍于己的日军激战，最后仅剩200余人突围。这次战役展现了中国军人视死如归的英雄气概。',4,1320,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (22,'豫湘桂会战','Battle of Henan-Hunan-Guangxi','河南、湖南、广西',32.6378000,113.8214000,'1944-04-17','1944-12-10',238,'蒋鼎文、汤恩伯','冈村宁次',500000,510000,300000,100000,'defeat','豫湘桂会战是抗战后期中国正面战场的一次大溃败，但也暴露了国民党军队的腐败，促使国共合作抗日。','豫湘桂会战是1944年日军为打通大陆交通线而发动的大规模进攻。由于国民党军队指挥失当、装备落后，战役以失败告终。但这次失败也促使中国军队进行改革，为最后的反攻奠定了基础。',4,1240,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (23,'湘西会战','Battle of West Hunan','湖南西部',27.5500000,109.7330000,'1945-04-09','1945-06-07',60,'王耀武、施中诚','板西一良',200000,80000,28000,28000,'victory','湘西会战是中国抗战的最后一次会战，中国军队取得完胜，标志着中国军队已完全掌握战场主动权。','湘西会战是抗日战争的最后一次大规模会战。中国军队装备精良，士气高昂，在王耀武指挥下取得完胜，歼敌2.8万余人。这次胜利标志着中国军队已完全掌握战场主动权，为最终胜利奠定了基础。',5,1680,1,1766237149,1766237149);
INSERT INTO `battle` (`id`, `name`, `english_name`, `location`, `latitude`, `longitude`, `start_date`, `end_date`, `duration_days`, `commander_cn`, `commander_jp`, `troops_cn`, `troops_jp`, `casualties_cn`, `casualties_jp`, `result`, `significance`, `description`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (24,'滇西反攻战役','Yunnan-Burma Campaign','云南西部',25.0406090,98.5783630,'1944-05-11','1945-01-20',255,'卫立煌、宋希濂','松山守备队',160000,50000,67000,21000,'victory','滇西反攻是中国军队首次大规模反攻作战，收复了大片国土，打通了中印公路。','1944年5月，中国远征军和驻印军发起滇西反攻，经过8个月激战，收复了滇西全部失地，打通了中印公路。这次反攻标志着中国军队由防御转入反攻，为最后胜利创造了条件。',5,1520,1,1766237149,1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (10,1,'淞沪会战第一阶段',1,'1937-08-13','1937-08-22','中国军队主动出击，进攻日军在上海的据点。','8月13日，中国军队向日军虹口、杨树浦等据点发起进攻。日军增援部队陆续到达。双方在市区展开激烈巷战。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (11,1,'淞沪会战第二阶段',2,'1937-08-23','1937-10-25','日军大规模增援，战线扩大到整个上海及周边地区。','日军在吴淞、川沙等地登陆，战线扩大。中日双方投入大量兵力，在罗店、大场等地展开激战。中国军队伤亡惨重，但始终坚守阵地。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (12,1,'淞沪会战第三阶段',3,'1937-10-26','1937-11-12','中国军队全线撤退，上海失守。','10月26日，日军在杭州湾金山卫登陆，对中国军队形成包围之势。中国军队被迫全线撤退。11月12日，上海失守。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (13,3,'台儿庄战役第一阶段',1,'1938-03-16','1938-03-23','日军进攻台儿庄，中国军队顽强抵抗。','3月16日，日军矶谷师团进攻台儿庄。中国军队在外围与日军激战，逐步后撤。3月23日，日军进入台儿庄城。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (14,3,'台儿庄战役第二阶段',2,'1938-03-24','1938-04-06','中国军队坚守台儿庄，与日军展开激烈巷战。','中国军队在台儿庄城内与日军展开逐屋争夺的巷战。双方伤亡惨重，战斗异常激烈。中国军队始终坚守阵地，等待反攻时机。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (15,3,'台儿庄战役第三阶段',3,'1938-04-06','1938-04-07','中国军队发起反攻，收复台儿庄。','4月6日夜，中国军队发起反攻，对日军形成包围。经过一夜激战，收复台儿庄，歼敌1万余人，取得抗战以来正面战场的最大胜利。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (16,6,'百团大战第一阶段',1,'1940-08-20','1940-09-10','破袭正太铁路为重点的交通破袭战。','8月20日，八路军105个团在华北地区同时发起攻击，重点破袭正太铁路。攻克日军据点多处，破坏铁路、公路数百公里。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (17,6,'百团大战第二阶段',2,'1940-09-22','1940-10-05','继续扩大战果，攻击日军据点。','八路军继续扩大战果，攻击日军据点和交通线。涞源、灵丘等战斗激烈，日军遭受重大损失。',1766237149);
INSERT INTO `battle_phase` (`id`, `battle_id`, `phase_name`, `phase_order`, `start_date`, `end_date`, `description`, `key_events`, `created_at`) VALUES (18,6,'百团大战第三阶段',3,'1940-10-06','1940-12-05','反\"扫荡\"作战。','日军集结重兵对根据地进行报复性\"扫荡\"。八路军进行反\"扫荡\"作战，保卫根据地。虽然付出较大代价，但最终粉碎了日军的\"扫荡\"。',1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (13,'张自忠','荩忱',1,'1891-08-11','1940-05-16','山东临清','陆军上将','第33集团军','general',NULL,'张自忠将军是抗日战争中牺牲的最高将领。他一生戎马，英勇善战，在枣宜会战中身先士卒，最后壮烈殉国，年仅49岁。毛泽东称其为\"抗战军人之魂\"。','淞沪会战、徐州会战、武汉会战、随枣会战、枣宜会战','追赠陆军上将、革命烈士','为国家民族死之决心，海不清，石不烂，决不半点改变！','湖北宜城南瓜店','重庆梅花山、北京卢沟桥',3450,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (14,'赵登禹','舜诚',1,'1898-01-01','1937-07-28','山东菏泽','陆军上将','第29军','general',NULL,'赵登禹将军是著名抗日将领，在喜峰口战役中率大刀队夜袭日军，取得重大胜利。七七事变后，在南苑战役中壮烈殉国，是第一位在抗战中牺牲的师级将领。','长城抗战、喜峰口战役、南苑战役','追赠陆军上将、革命烈士','军人战死沙场原是本分，没有什么值得悲伤的。','北京南苑','北京卢沟桥抗战纪念馆',2890,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (15,'佟麟阁','捷三',1,'1892-10-29','1937-07-28','河北高阳','陆军上将','第29军','general',NULL,'佟麟阁将军是著名抗日将领，七七事变后率部在南苑与日军激战，身中数弹仍坚持指挥战斗，最后壮烈殉国。他是抗战初期牺牲的最高将领之一。','长城抗战、南苑战役','追赠陆军上将、革命烈士','战死者光荣，偷生者耻辱；荣辱系于一人者轻，而系于国家民族者重。','北京南苑','北京卢沟桥抗战纪念馆',2670,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (16,'戴安澜','衍功',1,'1904-11-25','1942-05-26','安徽无为','陆军中将','第200师','general',NULL,'戴安澜将军是著名抗日将领，率第200师参加中国远征军入缅作战。在同古保卫战中重创日军，后在撤退途中遭遇日军伏击，身负重伤，壮烈殉国。','淞沪会战、徐州会战、武汉会战、昆仑关战役、同古保卫战','追赠陆军中将、革命烈士、国家一级英雄','现在孤军奋斗，决心全部牺牲，以报国家养育！','缅甸北部茅邦','安徽芜湖赭山公园',3120,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (17,'左权','纪权',1,'1905-03-15','1942-05-25','湖南醴陵','八路军副总参谋长','八路军总部','general',NULL,'左权将军是八路军高级将领，黄埔军校一期毕业，后留学苏联。回国后参加长征，抗战期间任八路军副总参谋长。1942年在反扫荡战斗中壮烈牺牲，是八路军在抗战中牺牲的最高将领。','平型关战役、百团大战','革命烈士、八路军高级将领','我牺牲了我的一切幸福为我的事业奋斗，请你相信这一道路是光明的、伟大的。','山西辽县十字岭','山西左权县烈士陵园',2980,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (18,'杨靖宇','马尚德',1,'1905-02-26','1940-02-23','河南确山','东北抗日联军第一路军总司令','东北抗日联军','general',NULL,'杨靖宇将军是著名抗日英雄，东北抗日联军创建人和领导人之一。在极端艰苦的条件下坚持游击战争，最后孤身一人与日军周旋5昼夜，壮烈牺牲。日军解剖其遗体，发现胃里只有草根、树皮和棉絮。','东北抗日游击战争','革命烈士、抗日民族英雄','革命就像火一样，任凭大雪封山，鸟兽藏迹，只要我们有火种，就能驱赶严寒，带来光明和温暖。','吉林濛江县保安村','吉林靖宇县杨靖宇烈士陵园',3680,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (19,'赵一曼','李坤泰',2,'1905-10-25','1936-08-02','四川宜宾','东北抗日联军第三军第二团政委','东北抗日联军','general',NULL,'赵一曼是著名女英雄，东北抗日联军的杰出领导人。1935年被日军逮捕，遭受酷刑仍坚贞不屈，1936年英勇就义。她在狱中给儿子写下遗书：\"母亲不用千言万语来教育你，就用实际行动来教育你。\"','东北抗日游击战争','革命烈士、抗日民族英雄','未惜头颅新故国，甘将热血沃中华。','黑龙江珠河县','黑龙江尚志市赵一曼纪念馆',3240,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (20,'狼牙山五壮士','',1,'1920-01-01','1941-09-25','河北','八路军战士','晋察冀军区第一军分区','soldier',NULL,'狼牙山五壮士是指在河北省易县狼牙山战斗中英勇抗击日军的八路军5位英雄：马宝玉、葛振林、宋学义、胡德林、胡福才。他们为掩护群众和主力部队转移，主动把敌人引向绝路，弹尽粮绝后跳崖殉国。','狼牙山战斗','革命烈士、抗日英雄','打倒日本帝国主义！中国共产党万岁！','河北易县狼牙山','河北易县狼牙山五勇士纪念塔',2560,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (21,'八女投江','',2,'1920-01-01','1938-10-05','东北','东北抗日联军战士','东北抗日联军第五军','soldier',NULL,'八女投江是指1938年10月，东北抗日联军第五军妇女团8名女战士：冷云、胡秀芝、杨贵珍、郭桂琴、黄桂清、王惠民、李凤善、安顺福，为掩护主力部队突围，主动吸引日军火力，最后弹尽援绝，宁死不屈，集体投江殉国。','东北抗日游击战争','革命烈士、抗日英雄','宁死不当亡国奴！','黑龙江牡丹江市林口县','黑龙江牡丹江市八女投江纪念碑',2780,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (22,'谢晋元','中民',1,'1905-04-26','1941-04-24','广东蕉岭','陆军中校','第88师524团','general',NULL,'谢晋元将军率\"八百壮士\"坚守上海四行仓库四天四夜，击退日军数十次进攻，鼓舞了全国军民的抗战士气。后被汪伪政府收买的叛徒刺杀身亡。','淞沪会战、四行仓库保卫战','革命烈士、抗日英雄','我们是中国人，我们要为祖国而战！','上海孤军营','上海四行仓库抗战纪念馆',2450,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (23,'彭雪枫','隆雍',1,'1907-09-09','1944-09-11','河南镇平','新四军第四师师长','新四军','general',NULL,'彭雪枫将军是新四军的杰出指挥员，在抗日战争和解放战争中屡建奇功。1944年在河南夏邑八里庄战斗中不幸中流弹牺牲，年仅37岁。','淮北抗日根据地建设、西华战役','革命烈士、新四军高级将领','为了人民的解放事业，我愿意献出自己的一切。','河南夏邑八里庄','河南镇平彭雪枫纪念馆',2120,1,1766237149,1766237149);
INSERT INTO `hero` (`id`, `name`, `alias`, `gender`, `birth_date`, `death_date`, `birthplace`, `rank`, `unit`, `category`, `photo`, `biography`, `major_battles`, `honors`, `famous_quotes`, `sacrifice_location`, `memorial_location`, `views`, `status`, `created_at`, `updated_at`) VALUES (24,'吉鸿昌','世五',1,'1895-10-18','1934-11-24','河南扶沟','陆军中将','抗日同盟军','general',NULL,'吉鸿昌将军是著名抗日将领，曾组织抗日同盟军。1933年在张家口抗击日军，后被国民党逮捕。1934年在北平英勇就义，临刑前写下绝命诗：\"恨不抗日死，留作今日羞。国破尚如此，我何惜此头！\"','长城抗战','革命烈士、抗日英雄','恨不抗日死，留作今日羞。国破尚如此，我何惜此头！','北京','河南扶沟吉鸿昌纪念馆',2340,1,1766237149,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (16,1,'指挥临沂战役','1938-03-14','山东临沂','1938年3月，张自忠指挥第59军在临沂与日军激战，击退日军板垣师团的进攻，为台儿庄战役的胜利创造了条件。','临沂战役的胜利，粉碎了日军南北夹击台儿庄的计划，为台儿庄大捷奠定了基础。',NULL,1,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (17,1,'坚守襄河防线','1940-05-01','湖北襄阳','1940年5月，张自忠率部坚守襄河防线，多次击退日军进攻，展现了高超的指挥艺术和顽强的战斗精神。','襄河防线的坚守，迟滞了日军的进攻，为中国军队争取了宝贵时间。',NULL,2,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (18,1,'壮烈殉国','1940-05-16','湖北宜城','1940年5月16日，张自忠在枣宜会战中身先士卒，率部与日军激战，最后壮烈殉国，成为抗战中牺牲的最高将领。','张自忠将军的牺牲，震惊全国。他的英勇事迹，激励着全国军民坚持抗战到底。',NULL,3,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (19,4,'指挥昆仑关战役','1939-12-18','广西南宁','1939年12月，戴安澜率第200师参加昆仑关战役，与日军激战，收复昆仑关，取得重大胜利。','昆仑关战役是抗战以来攻坚战的典范，戴安澜因此役成名，被誉为\"当代之标准青年将领\"。',NULL,1,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (20,4,'血战同古','1942-03-20','缅甸同古','1942年3月，戴安澜率第200师在缅甸同古与日军激战12天，以劣势装备重创日军，展现了中国军人的英勇气概。','同古保卫战打出了中国军人的威风，提高了中国的国际地位，英国首相丘吉尔称赞：\"立功异域，扬威国际\"。',NULL,2,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (21,4,'壮烈殉国','1942-05-26','缅甸茅邦','1942年5月26日，戴安澜在率部撤退途中遭遇日军伏击，身负重伤，壮烈殉国，年仅38岁。','戴安澜将军的牺牲，是中国军队的重大损失。毛泽东、蒋介石都为他题写了挽词，表达了对这位抗日英雄的敬意。',NULL,3,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (22,5,'指挥平型关战役','1937-09-25','山西灵丘','1937年9月25日，左权协助林彪指挥平型关战役，取得全面抗战以来的第一个大胜利。','平型关大捷打破了日军不可战胜的神话，极大地鼓舞了全国军民的抗战信心。',NULL,1,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (23,5,'指挥百团大战','1940-08-20','华北地区','1940年8月，左权协助彭德怀指挥百团大战，沉重打击了日军的\"囚笼政策\"。','百团大战是抗战期间八路军发动的规模最大的战役，提高了共产党和八路军的威望。',NULL,2,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (24,5,'壮烈殉国','1942-05-25','山西辽县','1942年5月25日，左权在反\"扫荡\"战斗中壮烈牺牲，是八路军在抗战中牺牲的最高将领。','左权将军的牺牲，是八路军的重大损失。他的英勇事迹，永远激励着中国人民。',NULL,3,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (25,6,'创建东北抗日联军','1933-01-01','东北地区','杨靖宇是东北抗日联军的创建人和领导人之一，在极端艰苦的条件下坚持游击战争。','东北抗日联军的建立，开辟了东北抗日游击战争的新局面，牵制了大量日军。',NULL,1,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (26,6,'孤身战斗五昼夜','1940-02-18','吉林濛江','1940年2月，杨靖宇在极端艰苦的条件下，孤身一人与日军周旋5昼夜，展现了钢铁般的意志。','杨靖宇将军的英勇事迹，震撼了敌人，教育了人民，成为中华民族精神的象征。',NULL,2,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (27,6,'英勇就义','1940-02-23','吉林濛江','1940年2月23日，杨靖宇在濛江县保安村壮烈牺牲。日军解剖其遗体，发现胃里只有草根、树皮和棉絮。','杨靖宇将军的牺牲，展现了中国人民不屈不挠的抗战精神，永远激励着后人。',NULL,3,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (28,7,'领导东北抗日游击战','1935-01-01','东北地区','赵一曼是东北抗日联军的杰出领导人，在极端艰苦的条件下领导抗日游击战争。','赵一曼的英勇斗争，沉重打击了日本侵略者，鼓舞了东北人民的抗日斗争。',NULL,1,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (29,7,'被捕后坚贞不屈','1935-11-15','黑龙江珠河','1935年11月，赵一曼在战斗中负伤被俘。在狱中，日军对她施以酷刑，但她始终坚贞不屈。','赵一曼在狱中的表现，展现了共产党员的坚强意志和崇高品格，成为中国妇女的光辉榜样。',NULL,2,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (30,7,'英勇就义','1936-08-02','黑龙江珠河','1936年8月2日，赵一曼在珠河县英勇就义，年仅31岁。临刑前，她给儿子写下了感人的遗书。','赵一曼的牺牲，是中国人民的重大损失。她的英勇事迹和遗书，教育和激励了无数中国人。',NULL,3,1766237149);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (31,1,'指挥临沂战役','1938-03-14','山东临沂','1938年3月，张自忠指挥第59军在临沂与日军激战，击退日军板垣师团的进攻，为台儿庄战役的胜利创造了条件。','临沂战役的胜利，粉碎了日军南北夹击台儿庄的计划，为台儿庄大捷奠定了基础。',NULL,1,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (32,1,'坚守襄河防线','1940-05-01','湖北襄阳','1940年5月，张自忠率部坚守襄河防线，多次击退日军进攻，展现了高超的指挥艺术和顽强的战斗精神。','襄河防线的坚守，迟滞了日军的进攻，为中国军队争取了宝贵时间。',NULL,2,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (33,1,'壮烈殉国','1940-05-16','湖北宜城','1940年5月16日，张自忠在枣宜会战中身先士卒，率部与日军激战，最后壮烈殉国，成为抗战中牺牲的最高将领。','张自忠将军的牺牲，震惊全国。他的英勇事迹，激励着全国军民坚持抗战到底。',NULL,3,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (34,4,'指挥昆仑关战役','1939-12-18','广西南宁','1939年12月，戴安澜率第200师参加昆仑关战役，与日军激战，收复昆仑关，取得重大胜利。','昆仑关战役是抗战以来攻坚战的典范，戴安澜因此役成名，被誉为\"当代之标准青年将领\"。',NULL,1,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (35,4,'血战同古','1942-03-20','缅甸同古','1942年3月，戴安澜率第200师在缅甸同古与日军激战12天，以劣势装备重创日军，展现了中国军人的英勇气概。','同古保卫战打出了中国军人的威风，提高了中国的国际地位，英国首相丘吉尔称赞：\"立功异域，扬威国际\"。',NULL,2,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (36,4,'壮烈殉国','1942-05-26','缅甸茅邦','1942年5月26日，戴安澜在率部撤退途中遭遇日军伏击，身负重伤，壮烈殉国，年仅38岁。','戴安澜将军的牺牲，是中国军队的重大损失。毛泽东、蒋介石都为他题写了挽词，表达了对这位抗日英雄的敬意。',NULL,3,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (37,5,'指挥平型关战役','1937-09-25','山西灵丘','1937年9月25日，左权协助林彪指挥平型关战役，取得全面抗战以来的第一个大胜利。','平型关大捷打破了日军不可战胜的神话，极大地鼓舞了全国军民的抗战信心。',NULL,1,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (38,5,'指挥百团大战','1940-08-20','华北地区','1940年8月，左权协助彭德怀指挥百团大战，沉重打击了日军的\"囚笼政策\"。','百团大战是抗战期间八路军发动的规模最大的战役，提高了共产党和八路军的威望。',NULL,2,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (39,5,'壮烈殉国','1942-05-25','山西辽县','1942年5月25日，左权在反\"扫荡\"战斗中壮烈牺牲，是八路军在抗战中牺牲的最高将领。','左权将军的牺牲，是八路军的重大损失。他的英勇事迹，永远激励着中国人民。',NULL,3,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (40,6,'创建东北抗日联军','1933-01-01','东北地区','杨靖宇是东北抗日联军的创建人和领导人之一，在极端艰苦的条件下坚持游击战争。','东北抗日联军的建立，开辟了东北抗日游击战争的新局面，牵制了大量日军。',NULL,1,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (41,6,'孤身战斗五昼夜','1940-02-18','吉林濛江','1940年2月，杨靖宇在极端艰苦的条件下，孤身一人与日军周旋5昼夜，展现了钢铁般的意志。','杨靖宇将军的英勇事迹，震撼了敌人，教育了人民，成为中华民族精神的象征。',NULL,2,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (42,6,'英勇就义','1940-02-23','吉林濛江','1940年2月23日，杨靖宇在濛江县保安村壮烈牺牲。日军解剖其遗体，发现胃里只有草根、树皮和棉絮。','杨靖宇将军的牺牲，展现了中国人民不屈不挠的抗战精神，永远激励着后人。',NULL,3,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (43,7,'领导东北抗日游击战','1935-01-01','东北地区','赵一曼是东北抗日联军的杰出领导人，在极端艰苦的条件下领导抗日游击战争。','赵一曼的英勇斗争，沉重打击了日本侵略者，鼓舞了东北人民的抗日斗争。',NULL,1,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (44,7,'被捕后坚贞不屈','1935-11-15','黑龙江珠河','1935年11月，赵一曼在战斗中负伤被俘。在狱中，日军对她施以酷刑，但她始终坚贞不屈。','赵一曼在狱中的表现，展现了共产党员的坚强意志和崇高品格，成为中国妇女的光辉榜样。',NULL,2,1766318178);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (45,7,'英勇就义','1936-08-02','黑龙江珠河','1936年8月2日，赵一曼在珠河县英勇就义，年仅31岁。临刑前，她给儿子写下了感人的遗书。','赵一曼的牺牲，是中国人民的重大损失。她的英勇事迹和遗书，教育和激励了无数中国人。',NULL,3,1766318178);
