INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (69,5,'壮烈殉国','1942-05-25','山西辽县','1942年5月25日，左权在反\"扫荡\"战斗中壮烈牺牲，是八路军在抗战中牺牲的最高将领。','左权将军的牺牲，是八路军的重大损失。他的英勇事迹，永远激励着中国人民。',NULL,3,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (70,6,'创建东北抗日联军','1933-01-01','东北地区','杨靖宇是东北抗日联军的创建人和领导人之一，在极端艰苦的条件下坚持游击战争。','东北抗日联军的建立，开辟了东北抗日游击战争的新局面，牵制了大量日军。',NULL,1,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (71,6,'孤身战斗五昼夜','1940-02-18','吉林濛江','1940年2月，杨靖宇在极端艰苦的条件下，孤身一人与日军周旋5昼夜，展现了钢铁般的意志。','杨靖宇将军的英勇事迹，震撼了敌人，教育了人民，成为中华民族精神的象征。',NULL,2,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (72,6,'英勇就义','1940-02-23','吉林濛江','1940年2月23日，杨靖宇在濛江县保安村壮烈牺牲。日军解剖其遗体，发现胃里只有草根、树皮和棉絮。','杨靖宇将军的牺牲，展现了中国人民不屈不挠的抗战精神，永远激励着后人。',NULL,3,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (73,7,'领导东北抗日游击战','1935-01-01','东北地区','赵一曼是东北抗日联军的杰出领导人，在极端艰苦的条件下领导抗日游击战争。','赵一曼的英勇斗争，沉重打击了日本侵略者，鼓舞了东北人民的抗日斗争。',NULL,1,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (74,7,'被捕后坚贞不屈','1935-11-15','黑龙江珠河','1935年11月，赵一曼在战斗中负伤被俘。在狱中，日军对她施以酷刑，但她始终坚贞不屈。','赵一曼在狱中的表现，展现了共产党员的坚强意志和崇高品格，成为中国妇女的光辉榜样。',NULL,2,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (75,7,'英勇就义','1936-08-02','黑龙江珠河','1936年8月2日，赵一曼在珠河县英勇就义，年仅31岁。临刑前，她给儿子写下了感人的遗书。','赵一曼的牺牲，是中国人民的重大损失。她的英勇事迹和遗书，教育和激励了无数中国人。',NULL,3,1766318580);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (76,1,'指挥临沂战役','1938-03-14','山东临沂','1938年3月，张自忠指挥第59军在临沂与日军激战，击退日军板垣师团的进攻，为台儿庄战役的胜利创造了条件。','临沂战役的胜利，粉碎了日军南北夹击台儿庄的计划，为台儿庄大捷奠定了基础。',NULL,1,1766327774);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (77,1,'坚守襄河防线','1940-05-01','湖北襄阳','1940年5月，张自忠率部坚守襄河防线，多次击退日军进攻，展现了高超的指挥艺术和顽强的战斗精神。','襄河防线的坚守，迟滞了日军的进攻，为中国军队争取了宝贵时间。',NULL,2,1766327774);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (78,1,'壮烈殉国','1940-05-16','湖北宜城','1940年5月16日，张自忠在枣宜会战中身先士卒，率部与日军激战，最后壮烈殉国，成为抗战中牺牲的最高将领。','张自忠将军的牺牲，震惊全国。他的英勇事迹，激励着全国军民坚持抗战到底。',NULL,3,1766327774);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (79,4,'指挥昆仑关战役','1939-12-18','广西南宁','1939年12月，戴安澜率第200师参加昆仑关战役，与日军激战，收复昆仑关，取得重大胜利。','昆仑关战役是抗战以来攻坚战的典范，戴安澜因此役成名，被誉为\"当代之标准青年将领\"。',NULL,1,1766327774);
INSERT INTO `hero_achievement` (`id`, `hero_id`, `title`, `event_date`, `location`, `description`, `impact`, `images`, `sort_order`, `created_at`) VALUES (80,4,'血战同古','1942-03-20','缅甸同古','1942年3月，戴安澜率第200师在缅甸同古与日军激战12天，以劣势装备重创日军，展现了中国军人的英勇气概。','同古保卫战打出了中国军人的威风，提高了中国的国际地位，英国首相丘吉尔称赞：\"立功异域，扬威国际\"。',NULL,2,1766327774);
