INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (11,'中国人民抗日战争纪念馆','museum','北京','北京','北京市丰台区卢沟桥宛平城内街101号',39.8514000,116.2133000,'1987-07-07',35000.00,'中国人民抗日战争纪念馆是全国唯一一座全面反映中国人民抗日战争历史的大型综合性专题纪念馆。馆内收藏各类文物达3万余件，展览面积近6000平方米。','包括历史照片、文献资料、实物展品等3万余件，其中珍贵文物2000余件。展品涵盖抗战时期的武器装备、军服徽章、文件电报、生活用品等。','9:00-16:30（周一闭馆）',0.00,'010-83893163','http://www.1937china.com',5680,4.8,1,1766237149,1766237149);
INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (12,'侵华日军南京大屠杀遇难同胞纪念馆','museum','江苏','南京','江苏省南京市建邺区水西门大街418号',32.0418000,118.7342000,'1985-08-15',74000.00,'侵华日军南京大屠杀遇难同胞纪念馆是为铭记侵华日军攻占南京后制造的南京大屠杀暴行而筹建的。馆内通过大量历史照片、文物、影像资料，揭露了日军的暴行。','馆藏文物史料20余万件，包括当年日军屠杀现场照片、幸存者证言、国际友人的记录等珍贵史料。','8:30-17:30（周一闭馆）',0.00,'025-86612230','http://www.nj1937.org',8920,4.9,1,1766237149,1766237149);
INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (13,'九一八历史博物馆','museum','辽宁','沈阳','辽宁省沈阳市大东区望花南街46号',41.7856000,123.4578000,'1991-09-18',36600.00,'九一八历史博物馆是为警示后人勿忘国耻而建立的专题博物馆。博物馆建筑外形为一本翻开的台历，标注着\"1931年9月18日\"，寓意着这一天永远铭刻在人民心中。','馆藏文物1000余件，包括九一八事变时期的历史照片、文献资料、实物等，真实再现了日本侵略者的罪行和中国人民的抗争历史。','9:00-16:00（周一闭馆）',0.00,'024-88338981','http://www.918museum.org.cn',4560,4.7,1,1766237149,1766237149);
INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (14,'台儿庄大战纪念馆','museum','山东','枣庄','山东省枣庄市台儿庄区沿河南路6号',34.5628000,117.7342000,'1993-04-08',34000.00,'台儿庄大战纪念馆是为纪念抗日战争初期著名的台儿庄大捷而建立的专题纪念馆。纪念馆通过大量珍贵文物和历史照片，再现了台儿庄大战的壮烈场面。','收藏抗战文物3000余件，包括当年战斗使用的武器、军服、电报、照片等，以及参战将士的回忆录和证言。','8:30-17:30',40.00,'0632-6679038','http://www.tezdzjng.com',3780,4.6,1,1766237149,1766237149);
INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (15,'平型关大捷纪念馆','museum','山西','大同','山西省大同市灵丘县白崖台乡',39.4422000,114.2340000,'1997-09-25',12000.00,'平型关大捷纪念馆是为纪念八路军115师在平型关伏击日军取得抗战首次大捷而建立的。纪念馆位于平型关战役旧址，真实再现了当年激战的场景。','收藏文物800余件，包括战斗遗留的武器弹药、战士使用的生活用品、战斗经过的文字记录和照片等。','8:00-18:00',20.00,'0352-8588888','',2890,4.5,1,1766237149,1766237149);
INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (16,'卢沟桥','monument','北京','北京','北京市丰台区卢沟桥城南街77号',39.8514000,116.2133000,'1189-01-01',5000.00,'卢沟桥始建于金代，是北京现存最古老的石造联拱桥。1937年7月7日，日军在此挑起事端，史称\"七七事变\"或\"卢沟桥事变\"，标志着中国全面抗战的开始。','卢沟桥本身就是珍贵的历史文物，桥上的石狮子形态各异，共有501只。桥头的宛平城墙上至今还保留着当年战斗的弹痕。','全天开放',20.00,'010-83893919','',6230,4.8,1,1766237149,1766237149);
