INSERT INTO `memorial` (`id`, `name`, `type`, `province`, `city`, `address`, `latitude`, `longitude`, `established_date`, `area`, `description`, `collections`, `opening_hours`, `ticket_price`, `contact_phone`, `website`, `views`, `rating`, `status`, `created_at`, `updated_at`) VALUES (60,'延安革命纪念馆','museum','陕西','延安','陕西省延安市宝塔区王家坪',36.5854000,109.4897000,'1950-01-01',29000.00,'延安革命纪念馆是中国共产党在延安时期革命历史的综合性纪念馆。展示了中国共产党领导全国人民进行抗日战争和解放战争的光辉历程。','馆藏文物3.5万余件，包括毛泽东、周恩来等领导人使用过的物品，以及大量抗战时期的文献、照片、实物等。','8:00-18:00（冬季17:30）',0.00,'0911-2332870','http://www.yagmjng.com',6780,4.9,1,1766327774,1766327774);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (13,'狼牙山五壮士的英雄事迹','legend','葛振林','狼牙山五壮士幸存者','1941年9月25日，八路军晋察冀军区第一军分区第一团七连六班的5名战士，为掩护群众和主力部队转移，主动把敌人引向狼牙山绝路，弹尽粮绝后纵身跳崖。','1941年秋，日伪军3500余人对晋察冀边区易县一带进行\"扫荡\"。9月25日，七连六班的马宝玉、葛振林、宋学义、胡德林、胡福才5名战士接受了掩护群众和主力部队转移的任务。他们边打边撤，把敌人引向狼牙山顶峰棋盘陀。在顶峰，他们英勇抗击，打退敌人多次冲锋。当子弹打光后，他们用石块还击。面对步步逼近的敌人，5位战士宁死不屈，砸烂枪支，高呼\"打倒日本帝国主义！\"\"中国共产党万岁！\"纵身跳下数十丈深的悬崖。马宝玉、胡德林、胡福才壮烈殉国，葛振林、宋学义被山腰的树枝挂住，幸免于难。',NULL,NULL,'葛振林口述，《人民日报》',NULL,NULL,NULL,1,4560,890,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (14,'八女投江的壮烈牺牲','legend','冷云','东北抗日联军战士','1938年10月，东北抗日联军第五军妇女团8名女战士为掩护主力部队突围，主动吸引日军火力，最后弹尽援绝，宁死不屈，集体投江殉国。','1938年10月，东北抗日联军第五军一部在牡丹江地区乌斯浑河渡口与日伪军千余人遭遇。妇女团指导员冷云和班长胡秀芝、杨贵珍、郭桂琴、黄桂清、王惠民、李凤善、安顺福等8名女战士，为掩护大部队突围，主动吸引日军火力，与敌人展开激战。她们在背水作战至弹尽援绝的情况下，面对日军的劝降，誓死不屈。冷云坚定地对大家说：\"同志们，我们是共产党员、抗联战士，宁死也不做俘虏！为祖国的解放而战死，是我们最大的光荣！\"她们毁掉枪支，挽臂涉入波涛滚滚的乌斯浑河，高唱着《国际歌》，集体沉江，壮烈殉国。牺牲时，她们中年龄最大的冷云23岁，最小的安顺福只有13岁。',NULL,NULL,'《东北抗日联军史料》',NULL,NULL,NULL,1,3890,720,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (15,'四行仓库八百壮士','legend','谢晋元','第88师524团团长','1937年10月，谢晋元率领第88师524团一营（实际420余人，对外号称800人）坚守上海四行仓库，孤军奋战四天四夜，击退日军数十次进攻。','1937年10月26日，淞沪会战已近尾声，中国军队大部撤退。为了向世界表明中国抗战到底的决心，第88师524团副团长谢晋元率领一营官兵420余人（对外号称800人），坚守苏州河北岸的四行仓库。四行仓库与公共租界仅一河之隔，战斗在各国记者和上海市民的注视下进行。谢晋元率部与日军激战四天四夜，击退敌人十余次进攻，毙伤日军200余人。战斗期间，上海市民冒着生命危险，将中国国旗和食物送入仓库。一位名叫杨惠敏的女童子军冒死将国旗送入仓库，次日清晨，国旗在四行仓库楼顶升起，极大地鼓舞了全国人民的抗战信心。10月30日，\"八百壮士\"奉命撤入租界，继续坚持抗战。',NULL,NULL,'《谢晋元传》',NULL,NULL,NULL,1,5230,1020,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (16,'赵一曼的英勇就义','memoir','赵一曼','东北抗日联军政委','赵一曼是东北抗日联军的杰出领导人，1935年被日军逮捕后，遭受酷刑仍坚贞不屈，1936年英勇就义，年仅31岁。','赵一曼原名李坤泰，1905年出生于四川宜宾。1926年加入中国共产党，后被派往东北从事革命工作。九一八事变后，她积极投身抗日斗争，成为东北抗日联军的杰出领导人。1935年11月，赵一曼在与日军作战中负伤被俘。在狱中，日军对她施以酷刑，用钢针刺她的手指，用烧红的烙铁烙她的皮肉，但她始终坚贞不屈，没有泄露任何党的机密。1936年6月，她在就医期间，在看护和医生的帮助下逃脱，但不久再次被捕。临刑前，她给儿子写下遗书：\"母亲对于你没有能尽到教育的责任，实在是遗憾的事情。母亲因为坚决地做了反满抗日的斗争，今天已经到了牺牲的前夕了。希望你，宁儿啊！赶快成人，来安慰你地下的母亲！在你长大成人之后，希望不要忘记你的母亲是为国而牺牲的！\"1936年8月2日，赵一曼在珠河县英勇就义，年仅31岁。',NULL,NULL,'《赵一曼烈士传》',NULL,NULL,NULL,1,4670,890,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (17,'张自忠将军的最后一战','legend','张克侠','第33集团军参谋','1940年5月，张自忠将军在枣宜会战中身先士卒，率部与日军激战，最后壮烈殉国，成为抗战中牺牲的最高将领。','1940年5月，日军集结15万兵力发动枣宜会战，企图切断通往重庆的运输线。第33集团军总司令张自忠将军亲率2000余人渡过襄河，与日军主力激战。5月16日，张自忠部被日军包围在南瓜店。面对数倍于己的敌人，张自忠毫不畏惧，亲自率队冲锋。激战中，他身中数弹，仍坚持指挥战斗。当左臂负伤后，他改用右手持枪射击。最后，他身中7弹，壮烈殉国，时年50岁。张自忠将军牺牲后，日军发现他的身份，为其举行了隆重的葬礼，以示敬意。蒋介石得知消息后，追赠张自忠为陆军上将。毛泽东称赞他为\"抗战军人之魂\"。重庆举行了10万人参加的追悼大会，全城下半旗致哀。张自忠将军的英勇事迹，永远激励着中国人民。',NULL,NULL,'《张自忠传》',NULL,NULL,NULL,1,5890,1150,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (18,'戴安澜将军血战同古','legend','戴复东','戴安澜之子','1942年3月，戴安澜率第200师在缅甸同古与日军激战12天，以劣势装备重创日军，展现了中国军人的英勇气概。','1942年3月，中国远征军第200师师长戴安澜奉命率部进入缅甸作战。3月20日，第200师到达同古，与日军第55师团遭遇。日军兵力数倍于我，且有飞机、坦克支援。戴安澜率部在同古与日军激战12天，击退日军20余次进攻，毙伤敌5000余人。战斗中，戴安澜身先士卒，多次亲临前线指挥。他对部下说：\"现在孤军奋斗，决心全部牺牲，以报国家养育！\"由于友军未能及时增援，第200师弹药、粮食耗尽，被迫突围。在撤退途中，戴安澜不幸遭遇日军伏击，身负重伤。5月26日，戴安澜在缅北茅邦村壮烈殉国，年仅38岁。毛泽东为他题写挽词：\"外侮需人御，将军赋采薇。师称机械化，勇夺虎罴威。浴血东瓜守，驱倭棠吉归。沙场竟殒命，壮志也无违。\"',NULL,NULL,'《戴安澜传》',NULL,NULL,NULL,1,4230,810,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (19,'杨靖宇将军的最后五天','legend','杨靖宇','东北抗日联军总司令','1940年2月，杨靖宇将军在极端艰苦的条件下，孤身一人与日军周旋5昼夜，最后壮烈牺牲。日军解剖其遗体，发现胃里只有草根、树皮和棉絮。','1940年2月，东北正值严冬，气温降至零下40度。杨靖宇率领的抗日联军在日伪军的\"围剿\"下，处境极其艰难。由于叛徒出卖，部队被打散，杨靖宇身边的战士一个个牺牲或失散。2月18日，最后两名战士也牺牲了，杨靖宇成了孤身一人。在零下40度的严寒中，没有粮食，没有棉衣，杨靖宇靠啃树皮、吃棉絮充饥。日军劝降，他严词拒绝：\"我是中国人，不能做亡国奴！\"2月23日，在濛江县保安村，杨靖宇被日军包围。他依托大树，用手枪与敌人激战，击毙击伤日伪军20余人。最后，身中数弹，壮烈牺牲，年仅35岁。日军残忍地将他的遗体解剖，发现他的胃里只有草根、树皮和棉絮，没有一粒粮食。连日军都被他的精神所震撼。杨靖宇将军的英雄事迹，永远激励着中国人民。',NULL,NULL,'《杨靖宇传》',NULL,NULL,NULL,1,6120,1280,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (20,'平型关大捷的胜利','legend','杨成武','八路军115师独立团团长','1937年9月25日，八路军115师在平型关伏击日军，歼敌1000余人，取得全面抗战以来的第一个大胜利。','1937年9月，日军第5师团沿平绥路西进，企图进攻太原。八路军115师师长林彪、副师长聂荣臻决定在平型关设伏，歼灭日军。9月24日夜，115师主力秘密进入平型关东北公路两侧山地设伏。25日拂晓，日军第21旅团辎重队进入伏击圈。林彪一声令下，八路军从山上冲下，与日军展开白刃战。战斗异常激烈，八路军战士冒着敌人的炮火，冲入敌群，与日军展开肉搏。经过一天激战，八路军全歼日军第21旅团1000余人，击毁汽车100余辆，缴获大量军用物资。平型关大捷是全面抗战以来中国军队取得的第一个大胜利，打破了\"日军不可战胜\"的神话，极大地鼓舞了全国军民的抗战信心。这一胜利证明，中国军队完全有能力战胜日本侵略者。',NULL,NULL,'《八路军抗战史》',NULL,NULL,NULL,1,5340,1050,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (21,'台儿庄大捷的辉煌','legend','池峰城','第31师师长','1938年春，中国军队在台儿庄与日军激战，经过半个多月的浴血奋战，取得抗战以来正面战场的最大胜利。','1938年3月，日军矶谷师团沿津浦路南下，进攻徐州。第五战区司令长官李宗仁决定在台儿庄与日军决战。3月23日，日军进攻台儿庄。第31师师长池峰城率部坚守台儿庄，与日军展开激烈巷战。战斗中，中国军队与日军逐屋争夺，有时一座房子要反复争夺数次。池峰城对部下说：\"士兵打完了，你就自己填进去！你填过了，我就来填进去！\"在最危急的时刻，第31师只剩下600余人，仍坚守阵地。4月6日，中国军队发起反攻，经过一夜激战，收复台儿庄，歼敌1万余人。台儿庄大捷是抗战以来正面战场取得的最大胜利，极大地鼓舞了全国军民的抗战士气，证明了中国军队完全有能力战胜日本侵略者。',NULL,NULL,'《台儿庄战役亲历记》',NULL,NULL,NULL,1,4780,920,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (22,'常德保卫战的悲壮','legend','余程万','第74军57师师长','1943年11月，第74军57师8000余人坚守常德16天，与数倍于己的日军激战，最后仅剩200余人突围。','1943年11月2日，日军11万人进攻常德。第74军57师师长余程万率8000余人坚守常德城。日军动用飞机、大炮、毒气弹，对常德城进行疯狂轰炸。余程万率部顽强抵抗，与日军展开激烈巷战。战斗中，中国军队伤亡惨重，但始终坚守阵地。余程万给上级的电报中写道：\"弹尽，援绝，人无，城已破。职率副师长、师附、政治部主任、参谋主任等固守中央银行，各团长划分区域，扼守一屋，作最后抵抗，誓死为止，并祝胜利。\"11月18日，常德城被日军攻占，但第57师仍在城内坚持战斗。11月21日夜，余程万率仅剩的200余人突围。12月9日，中国军队收复常德。常德保卫战中，第57师8000余人，最后仅剩200余人，展现了中国军人视死如归的英雄气概。',NULL,NULL,'《常德会战纪实》',NULL,NULL,NULL,1,4120,780,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (23,'百团大战的辉煌战果','legend','彭德怀','八路军副总司令','1940年8月，八路军在华北地区发动百团大战，参战部队达105个团约40万人，沉重打击了日军的\"囚笼政策\"。','1940年8月20日，八路军在华北地区同时对日军发动大规模进攻，史称\"百团大战\"。这次战役由八路军副总司令彭德怀指挥，参战部队达105个团约40万人，是抗战期间八路军发动的规模最大的战役。战役分为三个阶段：第一阶段以破袭正太铁路为重点，第二阶段继续扩大战果，第三阶段进行反\"扫荡\"作战。战役历时3个半月，进行大小战斗1824次，攻克据点2900余个，毙伤日伪军4万余人，俘虏日伪军1.8万余人，破坏铁路470余公里、公路1500余公里，缴获大量武器弹药。百团大战沉重打击了日军的\"囚笼政策\"，鼓舞了全国人民的抗战信心，提高了共产党和八路军的威望。但八路军也付出了伤亡1.7万余人的代价。',NULL,NULL,'《百团大战亲历记》',NULL,NULL,NULL,1,5670,1120,1,1766237149,1766237149);
INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (24,'新四军黄桥战役','legend','粟裕','新四军江南指挥部副指挥','1940年10月，新四军在黄桥地区与国民党顽固派军队作战，以少胜多，歼敌1.1万余人，巩固了苏北抗日根据地。','1940年10月，国民党顽固派军队1.5万人进攻新四军苏北根据地。新四军江南指挥部副指挥粟裕率7000余人在黄桥地区迎战。粟裕采取\"诱敌深入、各个击破\"的战术，将敌人引入黄桥地区。10月4日，战斗打响。新四军战士英勇作战，经过两天激战，歼敌1.1万余人，击毙敌89军军长李守维。黄桥战役的胜利，巩固了苏北抗日根据地，为新四军的发展创造了条件。战役中，黄桥人民全力支援新四军，烙制了30万个烧饼送往前线，留下了\"黄桥烧饼\"的佳话。',NULL,NULL,'《新四军抗战史》',NULL,NULL,NULL,1,3450,670,1,1766237149,1766237149);
