INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (128,'十位老兵的抗战故事','memoir','用户1',NULL,'“我们是让敌人防不胜防的‘夜老虎’”','爬上飞快的火车，像骑上奔驰的骏马，车站和铁道线上，是我们杀敌的好战场……”在中部战区总医院，百岁老人李德富哼唱起《弹起我心爱的土琵琶》。很难想象，这位在离休后喜欢唱歌、打太极、上老年大学的慈祥老人，曾经是令侵略者闻风丧胆的铁道游击队的一员。\r\n\r\n　　李德富1924年出生于河北，5岁时随家人投奔在山东当铁路工人的父亲李连生。1939年10月，父子俩跟随队伍加入了八路军苏鲁支队。\r\n\r\n　　1940年冬天至1943年春天，日伪军对鲁南抗日根据地采取“扫荡”、蚕食等手段，意图消灭当地的抗日武装，鲁南军民采取“敌进我进”战术，努力打破敌人封锁。“我们是让敌人防不胜防的‘夜老虎’，行军走路飞快又轻巧，擅长‘突然扑食’。”李德富说。\r\n\r\n　　1943年10月，李德富被任命为鲁南铁道大队长枪队政治指导员，这支大队就是大家耳熟能详的铁道游击队的原型。他和战友们采取灵活机动的游击战术，依托微山湖地区群众支持，扒火车、截铁路、炸桥梁，不断破坏日军运输线、截获军火物资。',NULL,NULL,NULL,NULL,NULL,NULL,0,0,0,2,1766329635,1766329635);
