INSERT INTO `story` (`id`, `title`, `category`, `author`, `author_role`, `summary`, `content`, `related_hero_id`, `related_battle_id`, `source`, `cover_image`, `audio_url`, `video_url`, `is_verified`, `views`, `likes`, `status`, `created_at`, `updated_at`) VALUES (133,'小英雄司号员崔振芳留下的军号','legend','用户1',NULL,'这把军号是小司号员崔振芳于1941年参加黄崖洞保卫战牺牲时留下的遗物，是国家二级文物。','这把军号是小司号员崔振芳于1941年参加黄崖洞保卫战牺牲时留下的遗物，是国家二级文物。\r\n\r\n崔振芳，山西洪洞县人。1937年冬，年仅14岁的他就参加了八路军，在八路军总部特务团当连部通信员。1940年被调到团部司号班学习司号和通信技术。18岁加入中国共产党。后在总部特务团三营七连当司号员，奉命随部队进驻黄崖洞兵工厂正面的翁圪郎，即南口。\r\n\r\n黄崖洞地处太行山脉，地形险要，易守难攻，被选作八路军总部兵工厂所在地。黄崖洞兵工厂，作为抗日战争时期华北敌后根据地创建最早、规模最大的兵工厂，为八路军坚持敌后抗战提供了有力的支援。\r\n\r\n1941年11月，日军为了摧毁这座兵工厂，纠集了5000余名重兵，分两路向黄崖洞扑来。11日拂晓，日军对总部特务团三营七连阵地发起偷袭未成，紧接着组织强攻。当敌人距八路军阵地百米左右时，司号员崔振芳吹响了军号，凭借天险，八路军阵地所有机枪、步枪一起开火，战士们把一颗颗手榴弹投向敌群，打退了敌人一次次疯狂的进攻。\r\n\r\n11日中午许，日军在数次进攻失败后，开始释放毒气。崔振芳发现了敌人图谋，及时发出了防毒信号，避免了部队遭受大的损失。\r\n\r\n下午三时左右，日军在炮火掩护下，分三路发起冲击，以八路军阵地右翼沿山根为重点，企图一举夺取南口。根据敌情变化，连长安排崔振芳、卫世华两位机智勇敢的小战士专门投掷手榴弹，两名新战士协助运送弹药。\r\n\r\n战斗中卫世华不幸负伤，崔振芳马上对战友进行了救护，随即又和另一名战士一起继续战斗。他一个人先后投出了100多枚手榴弹，炸死敌兵数十人，然而，自己也不幸牺牲。小号兵倒下了，与他相伴的只有那把心爱的军号。\r\n\r\n黄崖洞保卫战胜利了。这次战斗取得了敌我伤亡6∶1的辉煌战绩。小英雄司号员崔振芳为黄崖洞保卫战的胜利，献出了自己宝贵的生命。',NULL,NULL,NULL,NULL,NULL,NULL,0,0,0,0,1766331436,1766331436);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (24,'1931-09-18','九一八事变','battle','辽宁沈阳','1931年9月18日夜，日本关东军炸毁沈阳柳条湖附近的南满铁路路轨，反诬中国军队所为，随即炮轰北大营，进攻沈阳。这就是震惊中外的\"九一八事变\"。由于国民政府的不抵抗政策，东北三省在短短4个多月内全部沦陷。','日本关东军、东北军','九一八事变是日本帝国主义侵华的开端，标志着中国人民抗日战争的开始，也标志着世界反法西斯战争的开始。',NULL,NULL,NULL,5,4560,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (25,'1932-01-28','一二八事变','battle','上海','1932年1月28日，日军突然进攻上海闸北，十九路军奋起抵抗。战斗持续一个多月，中国军队英勇作战，给日军以沉重打击。最后在国际调停下，双方停火。','十九路军、日本海军陆战队','一二八事变是中国军队首次大规模抗击日本侵略的战斗，打破了日军不可战胜的神话，鼓舞了全国人民的抗日热情。',NULL,NULL,NULL,4,2340,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (26,'1933-03-01','长城抗战','battle','河北、热河','1933年3月，日军进攻长城各口。中国军队在喜峰口、古北口等地奋勇抵抗，其中喜峰口战役中，大刀队夜袭日军，取得重大胜利。','第29军、日本关东军','长城抗战是九一八事变后中国军队首次大规模抗日作战，虽然最终失利，但展现了中国军人的英勇气概。',NULL,NULL,NULL,4,1890,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (27,'1935-12-09','一二九运动','politics','北京','1935年12月9日，北平学生举行抗日救国示威游行，反对华北自治，要求停止内战、一致抗日。运动得到全国人民的支持和响应。','北平学生、全国民众','一二九运动促进了中华民族的觉醒，推动了抗日民族统一战线的建立，为全面抗战奠定了思想基础。',NULL,NULL,NULL,4,2120,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (28,'1936-12-12','西安事变','politics','陕西西安','1936年12月12日，张学良、杨虎城在西安扣留蒋介石，要求停止内战、联共抗日。经过中国共产党的调停，事变和平解决。','张学良、杨虎城、蒋介石、周恩来','西安事变的和平解决，标志着十年内战基本结束，抗日民族统一战线初步形成，为全面抗战奠定了基础。',NULL,NULL,NULL,5,3450,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (29,'1937-07-07','七七事变','battle','北京卢沟桥','1937年7月7日夜，日军在卢沟桥附近演习时，借口一名士兵失踪，要求进入宛平城搜查。遭到拒绝后，日军炮轰宛平城和卢沟桥。中国守军奋起抵抗，全面抗战由此爆发。','第29军、日本华北驻屯军','七七事变标志着日本全面侵华战争的开始，也标志着中国全面抗战的开始。中华民族到了最危险的时候。',NULL,NULL,NULL,5,5670,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (30,'1937-08-13','淞沪会战爆发','battle','上海','1937年8月13日，日军大举进攻上海。中国军队奋起抵抗，在上海及其周边地区与日军展开激战。战役历时三个月，是抗战初期规模最大的战役。','中国军队、日本上海派遣军','淞沪会战粉碎了日本\"三个月灭亡中国\"的狂妄计划，为中国争取了宝贵的战略准备时间。',NULL,NULL,NULL,5,3890,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (31,'1937-09-22','国共第二次合作','politics','全国','1937年9月22日，国民党中央通讯社发表《中国共产党为公布国共合作宣言》。23日，蒋介石发表谈话，承认中国共产党的合法地位。至此，抗日民族统一战线正式形成。','中国共产党、中国国民党','抗日民族统一战线的形成，实现了全民族抗战，为抗战胜利奠定了政治基础。',NULL,NULL,NULL,5,2780,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (32,'1937-09-25','平型关大捷','victory','山西灵丘','1937年9月25日，八路军第115师在平型关伏击日军第5师团第21旅团，歼敌1000余人，击毁汽车100余辆。这是全面抗战以来中国军队取得的第一个大胜利。','八路军第115师、日军第5师团','平型关大捷打破了日军不可战胜的神话，极大地鼓舞了全国军民的抗战信心。',NULL,NULL,NULL,5,4230,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (33,'1937-12-13','南京大屠杀','massacre','江苏南京','1937年12月13日，日军攻占南京后，对手无寸铁的中国平民和已放下武器的士兵进行了长达6周的大规模屠杀、抢劫、强奸等暴行，遇难者达30万人以上。','侵华日军、南京民众','南京大屠杀是人类历史上最黑暗的一页，是日本军国主义暴行的铁证。这段历史永远不能忘记。',NULL,NULL,NULL,5,6780,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (34,'1938-03-16','台儿庄战役开始','battle','山东台儿庄','1938年3月，日军进攻徐州，中国军队在台儿庄与日军激战。经过半个多月的浴血奋战，中国军队取得胜利，歼敌1万余人。','第五战区、日军第10师团','台儿庄大捷是抗战以来正面战场取得的最大胜利，极大地鼓舞了全国军民的抗战士气。',NULL,NULL,NULL,5,3560,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (35,'1938-06-11','武汉会战开始','battle','湖北武汉','1938年6月，日军集结35万兵力进攻武汉。中国军队投入110万兵力，在武汉周围与日军展开大规模会战。战役历时4个半月。','中国军队、日军华中派遣军','武汉会战是抗战初期规模最大、时间最长的战役，大量消耗了日军有生力量，为持久抗战奠定了基础。',NULL,NULL,NULL,5,2890,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (36,'1940-08-20','百团大战','battle','华北地区','1940年8月20日，八路军在华北地区发动百团大战。参战部队达105个团约40万人，主要目标是破坏日军的交通线，摧毁日伪军据点。','八路军、日军华北方面军','百团大战是抗战期间八路军在华北地区发动的规模最大的战役，沉重打击了日军的\"囚笼政策\"。',NULL,NULL,NULL,5,4120,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (37,'1941-12-07','珍珠港事件','battle','美国夏威夷','1941年12月7日，日本偷袭美国珍珠港海军基地，太平洋战争爆发。美国对日宣战，中国抗战成为世界反法西斯战争的重要组成部分。','日本海军、美国太平洋舰队','珍珠港事件标志着太平洋战争爆发，中国抗战从此不再孤军作战，获得了国际支持。',NULL,NULL,NULL,5,3780,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (38,'1942-01-01','《联合国家宣言》签署','diplomacy','美国华盛顿','1942年1月1日，中、美、英、苏等26国在华盛顿签署《联合国家宣言》，承诺共同对抗轴心国，不单独媾和。中国成为四大国之一。','中美英苏等26国','《联合国家宣言》的签署，标志着国际反法西斯统一战线正式形成，中国的国际地位大大提高。',NULL,NULL,NULL,4,2340,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (39,'1943-11-22','开罗会议','diplomacy','埃及开罗','1943年11月22日至26日，中美英三国首脑在开罗举行会议，讨论对日作战和战后安排。会议发表《开罗宣言》，明确日本必须归还侵占的中国领土。','蒋介石、罗斯福、丘吉尔','开罗会议确认了中国的大国地位，《开罗宣言》为战后收复失地提供了国际法依据。',NULL,NULL,NULL,5,2890,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (40,'1945-04-23','中共七大召开','politics','陕西延安','1945年4月23日至6月11日，中国共产党第七次全国代表大会在延安召开。大会确立了毛泽东思想为党的指导思想，为夺取抗战最后胜利和新民主主义革命胜利奠定了基础。','中国共产党','中共七大的召开，为抗战胜利和新中国的建立奠定了思想和组织基础。',NULL,NULL,NULL,5,2560,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (41,'1945-08-06','美国向广岛投掷原子弹','battle','日本广岛','1945年8月6日，美国向日本广岛投掷原子弹，造成大量人员伤亡。8月9日，又向长崎投掷第二颗原子弹。','美国、日本','原子弹的使用加速了日本投降的进程，但也造成了巨大的人道主义灾难。',NULL,NULL,NULL,5,3120,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (42,'1945-08-08','苏联对日宣战','battle','中国东北','1945年8月8日，苏联对日宣战，出兵中国东北。苏军迅速击溃日本关东军，为中国抗战胜利作出了重要贡献。','苏联红军、日本关东军','苏联出兵东北，加速了日本的投降，为中国抗战的最后胜利创造了条件。',NULL,NULL,NULL,5,2780,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (43,'1945-08-15','日本宣布无条件投降','victory','日本东京','1945年8月15日，日本天皇裕仁发表《终战诏书》，宣布接受《波茨坦公告》，无条件投降。中国人民抗日战争取得最后胜利。','日本政府','日本投降标志着中国人民抗日战争的伟大胜利，也标志着世界反法西斯战争的最后胜利。',NULL,NULL,NULL,5,8920,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (44,'1945-09-02','日本签署投降书','victory','日本东京湾','1945年9月2日，日本代表在停泊于东京湾的美国战列舰\"密苏里\"号上签署投降书。中国抗日战争暨世界反法西斯战争正式结束。','同盟国、日本','日本签署投降书，标志着第二次世界大战正式结束，中国人民取得了抗日战争的完全胜利。',NULL,NULL,NULL,5,7890,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (45,'1945-09-03','中国抗战胜利纪念日','victory','中国','1945年9月3日，中国举国欢庆抗战胜利。2014年，全国人大常委会将9月3日确定为中国人民抗日战争胜利纪念日。','全国人民','9月3日成为中国人民抗日战争胜利纪念日，永远铭记这一伟大胜利。',NULL,NULL,NULL,5,6780,1,1766237149,1766237149);
INSERT INTO `timeline_event` (`id`, `event_date`, `title`, `category`, `location`, `description`, `participants`, `impact`, `related_battle_id`, `related_hero_id`, `image`, `importance_level`, `views`, `status`, `created_at`, `updated_at`) VALUES (46,'1945-09-09','中国战区受降仪式','victory','江苏南京','1945年9月9日，中国战区日本投降签字仪式在南京举行。日本中国派遣军总司令冈村宁次向中国陆军总司令何应钦呈交投降书。','何应钦、冈村宁次','中国战区受降仪式标志着中国人民抗日战争取得完全胜利，洗刷了百年国耻。',NULL,NULL,NULL,5,5670,1,1766237149,1766237149);
